var gr = new GlideRecord('sys_user');
gr.addQuery('x_66281_donatenow_volunteer',true);
if(current.pickup_venue == 'Home'){
	gr.addQuery('location',current.location);
	while(gr.next()){
		current.assigned_to = gr.sys_id;
	}
}
else if(current.pickup_venue == 'office'){
	gr.addQuery('x_66281_donatenow_office_location',current.x_66281_donatenow_office_location);
	while(gr.next()){
		current.assigned_to = gr.sys_id;
	}
}
gr.update();

